    % Callbacks that handle component events
    methods (Access = private)

        % Button pushed function: DeleteButton
        function DeleteButtonPushed(app, event)
            [~,index] = ismember(app.ListBox.Value, app.ListBox.ItemsData);
            app.ListBox.Items(index) = [];
            app.ListBox.ItemsData(index) = [];
            cla(app.UIAxes);
            cla(app.UIAxes2);
            cla(app.UIAxes3);
            if (isempty(app.ListBox.Items) ~= true)
                ListBoxValueChanged(app, event);
            end 
        end

        % Button pushed function: AddButton
        function AddButtonPushed(app, ~)
            clear y;
            [filename,filepath] = uigetfile('*.wav');  %open a mat file
            filepath = strcat(filepath,filename);
            if (isempty(filepath) ~= true)
                app.tfile.Text = filepath;
                [y, fs] = audioread(filepath);
                t = (0:length(y)) / fs;
                t(end) = [];
                app.al.Text = strcat(num2str(t(end))," Second");
                plot(app.UIAxes,t, y);
                h=zoom(app.UIAxes);
                h.Motion = 'horizontal';
                h.Enable = 'on';
            
                itemList = app.ListBox.Items;
                itemData = app.ListBox.ItemsData;
                itemList{end+1} = filename;
                itemData{end+1} = filepath;
                app.ListBox.Items = itemList;
                app.ListBox.ItemsData = itemData;
            end
            
            
        end

        % Value changed function: ListBox
        function ListBoxValueChanged(app, ~)
            [~,index] = ismember(app.ListBox.Value, app.ListBox.ItemsData);
            value = app.ListBox.ItemsData{index};
            app.tfile.Text = value;
            [y, fs] = audioread(value);
            t = (0:length(y)) / fs;
            t(end) = [];
            plot(app.UIAxes,t, y);
            
            k = app.Slider.Value;
            global player
            player = audioplayer(y*k, fs);
            filePath = app.ListBox.Value;
            if (length(filePath) == 1)
                [y, fs] = audioread(filePath{1});
                global maxAMP;
                maxAMP = max(abs(y));
                audioLength = length(y)/fs;
                set(app.Alen,'Text',string(audioLength))
                set(app.StartSpinner,'Limits',[0,audioLength])
                set(app.EndSpinner,'Limits',[0,audioLength])
                set(app.EndSpinner,'Value',audioLength)
                set(app.AudioOperationPanel,'Enable','on')
                set(app.ReverseCheckBox,'Value',false)
                set(app.InvertCheckBox,'Value',false)
                set(app.Trims,'Value',false)
                set(app.SpeedSpinner,'Value',1)
                set(app.AmplitudeSpinner,'Value',1)
                set(app.PleaseselectfilefromlistboxLabel,'Text',filePath{1})
                set(app.StartSpinner,'Enable',false)
                set(app.EndSpinner,'Enable',false)
                set(app.NormalizeCheckBox,'Value',false)
            else
                set(app.AudioOperationPanel,'Enable','off')
            end
        end

        % Button pushed function: PlayButton
        function PlayButtonPushed(~, ~)
            global player
            play(player);
        end

        % Button pushed function: StopButton_2
        function StopButton_2Pushed(~, ~)
            global player
            stop(player);
        end

        % Value changed function: PauseResumeButton
        function PauseResumeButtonValueChanged(app, ~)
            value = app.PauseResumeButton.Value;
            global player
            if (value == 1) 
                pause(player);
            else
                resume(player);
            end
        end

        % Value changed function: Slider
        function SliderValueChanged(app, ~)
            global player
            [~,index] = ismember(app.ListBox.Value, app.ListBox.ItemsData);
            app.tfile.Text = string(isplaying(player));
            if (index ~= 0.0 && isplaying(player) ~= false)
                value = app.ListBox.ItemsData{index};
                pause(player);
                NewStart = get(player, 'CurrentSample')+1;
                stop(player);
                k = app.Slider.Value;
                [y, fs] = audioread(value);
                player = audioplayer(y*k, fs);
                play(player, NewStart);
            end
        end

        % Button pushed function: MergeselectedfilesButton
        function MergeselectedfilesButtonPushed(app, ~)
            
            filePath = app.ListBox.Value;
            global player
            global mergeAudio
            global mergeFs
            if (length(filePath) <= 0)
                return
            end
            if (length(filePath) == 1)
                [y, fs] = audioread(filePath{1});
                t = (0:length(y)) / fs;
                t(end) = [];
                plot(app.UIAxes2,t, y); 
                %plot(app.UIAxes3,t,y);
                x = y;
                df = fs/length(x);
                f = -fs/2:df:fs/2-df;
                fftA = fftshift(fft(x))/length(fft(x));
                
                plot(app.UIAxes3, f, abs(fftA));
                %title ('FFT Spectrum');
                k = app.Slider.Value;
                player = audioplayer(y*k, fs);
                mergeAudio = y;
                mergeFs = fs;
                set(app.ExportMergeAudiotoFileButton,'Enable','on');
               return
            end
            
            for i = 2:length(filePath)
                if (i == 2)
                    [y1, fs1] = audioread(filePath{1});
                    
                    [y2, fs2] = audioread(filePath{2});
                    
                    lenY = size(y1, 1);
                    lenZ = size(y2, 1);
                    len = max(lenY, lenZ);
                    S = zeros(len, size(y1, 2));
                    S(1:lenY, :) = y1;
                    S(1:lenZ, :) = S(1:lenZ, :) + y2;
                    maxValue = max(abs(S(:)));
                    S = S / maxValue;
                    if (length(y1) >= length(y2))
                        t = (1:length(y1)) / fs1;
                        fs = fs1;
                    else
                        t = (1:length(y2)) / fs2;
                        fs = fs2;
                    end
                else
                    [y, fs_] = audioread(filePath{i});
                    y_dum = S;
                    fs_dum = fs;
                    
                    lenY = size(y_dum, 1);
                    lenZ = size(y, 1);
                    len = max(lenY, lenZ);
                    S = zeros(len, size(y_dum, 2));
                    S(1:lenY, :) = y_dum;
                    S(1:lenZ, :) = S(1:lenZ, :) + y;
                    maxValue = max(abs(S(:)));
                    S = S / maxValue;
                    if (length(y_dum) >= length(y))
                        t = (1:length(y_dum)) / fs;
                        fs = fs_dum;
                    else
                        t = (1:length(y)) / fs_;
                        fs = fs_;
                    end
                end
            end
            plot(app.UIAxes2,t, S);
            x = S;
            Fs = fs;
            df = Fs/length(S);
            f = -Fs/2:df:Fs/2-df;
            fftA = fftshift(fft(x))/length(fft(x));
            
            plot(app.UIAxes3, f, abs(fftA));
            k = app.Slider.Value;
            player = audioplayer(S*k, Fs);
            mergeAudio = S;
            mergeFs = Fs;
            set(app.ExportMergeAudiotoFileButton,'Enable','on');
        end

        % Button pushed function: SaveButton
        function SaveButtonPushed(app, ~)
            filePath = app.ListBox.Value;
            if (length(filePath) == 1)
                set(app.AudioOperationPanel,'Enable','off')
                if (app.ReverseCheckBox.Value)
                    [y, fs] = audioread(filePath{1});
                    y2 = flipud(y);
                    audiowrite(filePath{1},y2,fs);
                end
                if (app.InvertCheckBox.Value)
                    [y, fs] = audioread(filePath{1});
                    y2 = y * -1;
                    audiowrite(filePath{1},y2,fs);
                end
                if (app.Trims.Value)
                    [y, fs] = audioread(filePath{1});
                    samples=[fix(app.StartSpinner.Value * fs)+1,round(app.EndSpinner.Value * fs)];
                    [y1,fs2] = audioread(filePath{1},samples);
                    audiowrite(filePath{1},y1,fs2);
                end
                [y, fs] = audioread(filePath{1});
                newSampleRate = app.SpeedSpinner.Value * fs;
                if (newSampleRate >= 80 && newSampleRate <= 1000000) 
                    y2 = y * app.AmplitudeSpinner.Value;
                    audiowrite(filePath{1},y2,fix(app.SpeedSpinner.Value * fs));
                end
                set(app.ListBox, 'Value', {});
            end           
            
        end

        % Button pushed function: SaveasButton
        function SaveasButtonPushed(app, event)
            filter = {'*.wav';'*.*'};
            [file, path] = uiputfile(filter);
            filetarget = strcat(path,file);
            
            filePath = app.ListBox.Value;
            if (length(filePath) == 1)
                set(app.AudioOperationPanel,'Enable','off')
                [y, fs] = audioread(filePath{1});
                audiowrite(filetarget,y,fs);
                if (app.ReverseCheckBox.Value)
                    [y, fs] = audioread(filetarget);
                    y2 = flipud(y);
                    audiowrite(filetarget,y2,fs);
                end
                if (app.InvertCheckBox.Value)
                    [y, fs] = audioread(filetarget);
                    y2 = y * -1;
                    audiowrite(filetarget,y2,fs);
                    
                end
                if (app.Trims.Value)
                    [~, fs] = audioread(filetarget);
                    samples=[fix(app.StartSpinner.Value * fs)+1,round(app.EndSpinner.Value * fs)];
                    [y1,fs2] = audioread(filetarget,samples);
                    audiowrite(filetarget,y1,fs2);
                end
                [y, fs] = audioread(filetarget);
                newSampleRate = app.SpeedSpinner.Value * fs;
                if (newSampleRate >= 80 && newSampleRate <= 1000000) 
                    y2 = y * app.AmplitudeSpinner.Value;
                    audiowrite(filetarget,y2,fix(app.SpeedSpinner.Value * fs));
                end
                set(app.ListBox, 'Value', {});
                itemList = app.ListBox.Items;
                itemData = app.ListBox.ItemsData;
                itemList{end+1} = file;
                itemData{end+1} = filetarget;
                app.ListBox.Items = itemList;
                app.ListBox.ItemsData = itemData;
            end
        end

        % Value changed function: Trims
        function TrimsValueChanged(app, ~)
            value = app.Trims.Value;
            if (value)
                set(app.StartSpinner,'Enable',true)
                set(app.EndSpinner,'Enable',true)
            else
                set(app.StartSpinner,'Enable',false)
                set(app.EndSpinner,'Enable',false)
            end
        end

        % Value changed function: NormalizeCheckBox
        function NormalizeCheckBoxValueChanged(app, event)
            value = app.NormalizeCheckBox.Value;
            if (value)
                global maxAMP;
                newAMP = 1/max(maxAMP);
                set(app.AmplitudeSpinner,'Value',newAMP);
            else
                
            end
        end

        % Value changed function: AmplitudeSpinner
        function AmplitudeSpinnerValueChanged(app, ~)
            value = app.AmplitudeSpinner.Value;
            set(app.NormalizeCheckBox,'Value',false);
        end

        % Button pushed function: ExportMergeAudiotoFileButton
        function ExportMergeAudiotoFileButtonPushed(~, ~)
            global mergeAudio
            global mergeFs
            filter = {'*.wav';'*.*'};
            [file, path] = uiputfile(filter);
            filetarget = strcat(path,file);
            audiowrite(filetarget,mergeAudio,mergeFs);
        end
    end

